
public class ForLoopInJava {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// print msg 10 time
		
		// print even and odd number 1 to 100
		//boolean flg=true;
		for(int i=1;i<100;i++) {
		  if(i%2==0) {
			  System.out.println(i+" is even number");
		  } else {
			  System.out.println(i+" is odd number");
		  }
		}

	}

}
